package main

import (
	"encoding/hex"
	"testing"
)

func TestXLNABI(t *testing.T) {
	ab := XLightNodeAbi
	t.Log(ab.Constructor)
	t.Log("events")
	for n, e := range ab.Events {
		t.Log(n, "=>", e)
	}
	t.Log("methods")
	for n, e := range ab.Methods {
		t.Log(n, "=>", e)
	}
	t.Log("errors")
	for n, e := range ab.Errors {
		t.Log(n, "=>", e)
	}
	t.Log(ab.Fallback)
	t.Log(ab.Receive)
}

func TestXLNVerify(t *testing.T) {
	bs, _ := hex.DecodeString("")
	obj1 := new(struct {
		Data []byte `abi:"proofBytes"`
	})
	if err := XLightNodeAbi.UnpackInput(obj1, xVerifyReceiptData, bs[4:]); err != nil {
		t.Fatal(err)
	}
	obj2 := new(struct {
		Data TKMReceiptData `abi:"receiptData"`
	})
	if err := XLightNodeAbi.UnpackInput(obj2, xVerifyReceiptStruct, obj1.Data); err != nil {
		t.Fatal(err)
	}
	t.Logf("%+v", obj2)
}

func TestXLNUpdate(t *testing.T) {
	bs, _ := hex.DecodeString("")
	obj := new(struct {
		Data XCommProofData `abi:"commData"`
	})
	if err := XLightNodeAbi.UnpackInput(obj, xUpdateCommName, bs[4:]); err != nil {
		t.Fatal(err)
	}
	t.Logf("%+v", obj.Data)
}
